mapper.update:
insert into t_ebook_snapshot(ebook_id, date, view_count, vote_count, view_increase, vote_increase) select t1.id, curdate(), 0, 0, 0, 0 from t_ebook t1 where not exists(select 1 from t_ebook_snapshot t2 where t1.id = t2.ebook_id and t2.date = curdate());
update t_ebook t1, t_ebook_snapshot t2 set t2.vote_count = t1.vote_count, t2.view_count = t1.view_count where t1.id = t2.ebook_id and t2.date = curdate();
update t_ebook_snapshot t1 left join (select ebook_id, view_count, vote_count from t_ebook_snapshot where date = date_sub(curdate(), interval 1 day)) t2 on t1.ebook_id = t2.ebook_id set t1.view_increase = t1.view_count - ifnull(t2.view_count, 0), t1.vote_increase = t1.vote_count - ifnull(t2.vote_count, 0) where t1.date = curdate();
